function dxDrawTextOnElement(TheElement,text,height,distance,R,G,B,alpha,size,font,...)	local x, y, z = getElementPosition(TheElement)	local x2, y2, z2 = getCameraMatrix()	local distance = distance or 20	local height = height or 1		if (isLineOfSightClear(x, y, z+2, x2, y2, z2, ...)) then		local sx, sy = getScreenFromWorldPosition(x, y, z+height)		if(sx) and (sy) then			local distanceBetweenPoints = getDistanceBetweenPoints3D(x, y, z, x2, y2, z2)			if(distanceBetweenPoints < distance) then				dxDrawText(text, sx+2, sy+2, sx, sy, tocolor(R or 255, G or 255, B or 255, alpha or 255), (size or 1)-(distanceBetweenPoints / distance), font or "arial", "center", "center")			end		end	endendlocal randomPed = createPed(295,-1980.8861083984,168.37161254883,27.713445663452,90)setElementFrozen(randomPed,true)addEventHandler("onClientPedDamage", randomPed, cancelEvent)PlayasEmre = createPed(295,-1998,210.5,29.3,262.003)--name = createPed(295,-1996.3,213.39999,29.3,214.009)text1 = "PlayasEmre [Entwickler] sagt Gib mir dein Sozialgeld"--text2 = "text"		setTimer(setPedAnimation,500,1,PlayasEmre, "gangs", "dealer_deal", -1, true, true, false)--setTimer(setPedAnimation,500,1,text2, "cop_ambient", "Coplook_loop", -1, true, true, false)		setElementFrozen(PlayasEmre,true)--setElementFrozen(text2,true)addEventHandler("onClientPedDamage", PlayasEmre, cancelEvent)--addEventHandler("onClientPedDamage", text2, cancelEvent)addEventHandler("onClientRender", root, function ()	if getElementDimension ( localPlayer ) < 1 and getElementInterior ( localPlayer ) < 1 then		dxDrawTextOnElement(randomPed,"Herzlich Willkommen beim Fahrzeugverleih!",1,20,255,255,255,255,1.5,"sans")	end		if getElementDimension ( localPlayer ) < 1 and getElementInterior ( localPlayer ) < 1 then		dxDrawTextOnElement(PlayasEmre,tostring(text1),1,20,255,0,0,255,1.5,"sans")	end		--[[if getElementDimension ( localPlayer ) < 1 and getElementInterior ( localPlayer ) < 1 then		dxDrawTe